"use client";

import Link from "next/link";
import { usePathname } from "next/navigation";
import { cn } from "../lib/utils";
import { useEffect, useState } from "react";
import { motion, useScroll, useSpring, AnimatePresence } from "framer-motion";

const links = [
    { href: "/", label: "HOME" },
    { href: "/about", label: "ABOUT" },
    { href: "/services", label: "SERVICES" },
    { href: "/faq", label: "FAQ" },
    { href: "/contact", label: "CONTACT" },
];

export default function Header() {
    const pathname = usePathname();
    const [mounted, setMounted] = useState(false);
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [scrolled, setScrolled] = useState(false);

    const { scrollYProgress } = useScroll();
    const scaleX = useSpring(scrollYProgress, {
        stiffness: 100,
        damping: 30,
        restDelta: 0.001
    });

    useEffect(() => {
        setMounted(true);
        if ("scrollRestoration" in history) {
            history.scrollRestoration = "manual";
        }
        window.scrollTo(0, 0);

        const handleScroll = () => {
            setScrolled(window.scrollY > 20);
        };
        window.addEventListener("scroll", handleScroll);
        return () => window.removeEventListener("scroll", handleScroll);
    }, []);

    // Lock body scroll when menu is open
    useEffect(() => {
        if (isMenuOpen) {
            document.body.style.overflow = "hidden";
        } else {
            document.body.style.overflow = "unset";
        }
    }, [isMenuOpen]);

    return (
        <>
            <header
                className={cn(
                    "fixed top-0 left-0 w-full z-50 px-6 md:px-[8vw] flex justify-between items-center transition-all duration-500 will-change-transform",
                    scrolled
                        ? "bg-white/80 backdrop-blur-lg py-4 border-b border-black/5"
                        : "bg-transparent py-6"
                )}
            >
                {/* Logo */}
                <Link
                    href="/"
                    className="relative z-50 text-3xl font-serif font-bold tracking-tighter text-[#111]"
                    onClick={() => setIsMenuOpen(false)}
                >
                    Dakeek.
                </Link>

                {/* Desktop Nav */}
                <nav className="hidden md:block">
                    <ul className="flex gap-8 text-sm font-medium tracking-wide">
                        {links.map((link) => {
                            const isActive = mounted && pathname === link.href;
                            return (
                                <li key={link.href} className="relative group">
                                    <Link
                                        href={link.href}
                                        className={cn(
                                            "relative z-10 transition-colors duration-300 hover:text-[#A18262]",
                                            isActive ? "text-[#111] font-bold" : "text-[#666] hover:text-[#A18262]"
                                        )}
                                    >
                                        {link.label}
                                    </Link>
                                    <span
                                        className={cn(
                                            "absolute -bottom-1 left-0 w-full h-[1px] bg-[#A18262] transform origin-left transition-transform duration-300 ease-out",
                                            isActive ? "scale-x-100" : "scale-x-0 group-hover:scale-x-100"
                                        )}
                                    />
                                </li>
                            );
                        })}
                    </ul>
                </nav>

                {/* Mobile Menu Toggle (Custom Animated Icon) */}
                <button
                    className="md:hidden relative z-50 w-10 h-10 flex flex-col justify-center items-center gap-[6px] group"
                    onClick={() => setIsMenuOpen(!isMenuOpen)}
                    aria-label="Toggle Menu"
                >
                    <motion.span
                        animate={isMenuOpen ? { rotate: 45, y: 8 } : { rotate: 0, y: 0 }}
                        className="w-8 h-[2px] bg-[#111] block rounded-full"
                    />
                    <motion.span
                        animate={isMenuOpen ? { opacity: 0 } : { opacity: 1 }}
                        className="w-8 h-[2px] bg-[#111] block rounded-full"
                    />
                    <motion.span
                        animate={isMenuOpen ? { rotate: -45, y: -8 } : { rotate: 0, y: 0 }}
                        className="w-8 h-[2px] bg-[#111] block rounded-full"
                    />
                </button>

                {/* Mobile Scroller */}
                <motion.div
                    className="absolute bottom-0 left-0 h-[2px] bg-[#A18262] origin-left z-50"
                    style={{ scaleX, width: "100%" }}
                />
            </header>

            {/* Mobile Menu Overlay */}
            <AnimatePresence>
                {isMenuOpen && (
                    <motion.div
                        initial={{ opacity: 0, y: "-100%" }}
                        animate={{ opacity: 1, y: "0%" }}
                        exit={{ opacity: 0, y: "-100%" }}
                        transition={{ duration: 0.4, ease: [0.22, 1, 0.36, 1] }}
                        className="fixed inset-0 z-40 bg-[#FAFAF9] flex flex-col pt-32 px-6 md:hidden"
                    >
                        <div className="flex flex-col gap-8">
                            {links.map((link, index) => (
                                <motion.div
                                    key={link.href}
                                    initial={{ opacity: 0, x: -20 }}
                                    animate={{ opacity: 1, x: 0 }}
                                    transition={{ delay: 0.1 + index * 0.05 }}
                                >
                                    <Link
                                        href={link.href}
                                        onClick={() => setIsMenuOpen(false)}
                                        className="text-4xl font-serif text-[#111] hover:text-[#A18262] transition-colors"
                                    >
                                        {link.label}
                                    </Link>
                                </motion.div>
                            ))}
                        </div>

                        {/* Mobile Footer Info */}
                        <motion.div
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            transition={{ delay: 0.5 }}
                            className="mt-auto pb-12 space-y-4"
                        >
                            <div className="h-[1px] w-full bg-[#E5E5E5] mb-6" />
                            <p className="text-[#86868b] text-sm uppercase tracking-widest">Get in Touch</p>
                            <p className="text-xl text-[#111] font-mono">800-DAKEEK</p>
                            <Link href="/contact" onClick={() => setIsMenuOpen(false)} className="block w-full py-4 bg-[#111] text-white text-center font-bold uppercase tracking-widest mt-4">
                                Book Now
                            </Link>
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>
        </>
    );
}

"use client";

import { useRef, useMemo, Suspense } from "react";
import { Canvas, useFrame, useThree, extend } from "@react-three/fiber";
import { shaderMaterial, Environment, Float, Stars } from "@react-three/drei";
import * as THREE from "three";

// --- GLSL SHADERS ---

const LiquidMetalMaterial = shaderMaterial(
    {
        uTime: 0,
        uMouse: new THREE.Vector2(0, 0),
        uResolution: new THREE.Vector2(1, 1),
        uColor1: new THREE.Color("#A18262"), // Default Gold
        uColor2: new THREE.Color("#F0F0F0"), // Default Silver/White
        uEnvironment: null,
    },
    // Vertex Shader
    `
    varying vec2 vUv;
    varying float vDisplacement;
    varying vec3 vNormal;
    varying vec3 vPosition;

    uniform float uTime;
    uniform vec2 uMouse;

    // Simplex Noise 3D function (Standard substitution)
    vec3 mod289(vec3 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
    vec4 mod289(vec4 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
    vec4 permute(vec4 x) { return mod289(((x*34.0)+1.0)*x); }
    vec4 taylorInvSqrt(vec4 r) { return 1.79284291400159 - 0.85373472095314 * r; }
    float snoise(vec3 v) {
        const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;
        const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);
        vec3 i  = floor(v + dot(v, C.yyy) );
        vec3 x0 = v - i + dot(i, C.xxx) ;
        vec3 g = step(x0.yzx, x0.xyz);
        vec3 l = 1.0 - g;
        vec3 i1 = min( g.xyz, l.zxy );
        vec3 i2 = max( g.xyz, l.zxy );
        vec3 x1 = x0 - i1 + C.xxx;
        vec3 x2 = x0 - i2 + C.yyy;
        vec3 x3 = x0 - D.yyy;
        i = mod289(i);
        vec4 p = permute( permute( permute(
                    i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
                + i.y + vec4(0.0, i1.y, i2.y, 1.0 ))
                + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));
        float n_ = 0.142857142857;
        vec3  ns = n_ * D.wyz - D.xzx;
        vec4 j = p - 49.0 * floor(p * ns.z * ns.z);
        vec4 x_ = floor(j * ns.z);
        vec4 y_ = floor(j - 7.0 * x_ );
        vec4 x = x_ *ns.x + ns.yyyy;
        vec4 y = y_ *ns.x + ns.yyyy;
        vec4 h = 1.0 - abs(x) - abs(y);
        vec4 b0 = vec4( x.xy, y.xy );
        vec4 b1 = vec4( x.zw, y.zw );
        vec4 s0 = floor(b0)*2.0 + 1.0;
        vec4 s1 = floor(b1)*2.0 + 1.0;
        vec4 sh = -step(h, vec4(0.0));
        vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
        vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;
        vec3 p0 = vec3(a0.xy,h.x);
        vec3 p1 = vec3(a0.zw,h.y);
        vec3 p2 = vec3(a1.xy,h.z);
        vec3 p3 = vec3(a1.zw,h.w);
        vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
        p0 *= norm.x;
        p1 *= norm.y;
        p2 *= norm.z;
        p3 *= norm.w;
        vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
        m = m * m;
        return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1),
                                    dot(p2,x2), dot(p3,x3) ) );
    }

    void main() {
      vUv = uv;
      vPosition = position;
      // Fluid Distortion Pattern - BALANCED FLOW
      // Visible movement but smooth
      float n = snoise(vec3(position.x * 1.1, position.y * 1.1, uTime * 0.14));
      
      // Final Displacement - BALANCED DEPTH
      // Deep enough to see, smooth enough to look liquid
      float displacement = n * 0.6;
      vDisplacement = displacement;
      
      // Recalculate Normal (Approximation)
      vec3 newPos = position + normal * displacement;
      gl_Position = projectionMatrix * modelViewMatrix * vec4(newPos, 1.0);
      
      // Pass normal for lighting
      vNormal = normalize(normalMatrix * normal); 
    }
  `,
    // Fragment Shader
    `
    varying vec2 vUv;
    varying float vDisplacement;
    varying vec3 vNormal;
    
    uniform vec3 uColor1;
    uniform vec3 uColor2;
    uniform float uTime;
    
    void main() {
      // 1. Base Metal/Liquid Mix (Light Theme)
      // Clear separation but smooth gradient
      vec3 color = mix(uColor2, uColor1, vDisplacement * 1.25 + 0.15); 
      
      // 2. Shadows/AO approximation
      // Moderate shadowing to define the waves
      color -= vec3(vDisplacement * 0.25); 
      
      // 3. Fake Reflection / Specular highlight (Cheap PBR)
      vec3 lightDir = normalize(vec3(0.5, 1.0, 2.0)); 
      float diff = max(dot(vNormal, lightDir), 0.0);
      
      // 4. Chromatic Aberration - Visible but Clean
      float aberration = vDisplacement * 0.02;
      vec3 r = color * (1.0 + aberration);
      vec3 g = color;
      vec3 b = color * (1.0 - aberration);
      
      // 5. Fresnel Effect (Rim Light)
      // Soft but bright sheen (Power 2.5 for wider falloff)
      float fresnel = pow(1.0 - dot(vNormal, vec3(0.0, 0.0, 1.0)), 2.5);
      vec3 finalColor = vec3(r.r, g.g, b.b);
      finalColor += vec3(1.0) * fresnel * 0.6; 
      
      gl_FragColor = vec4(finalColor, 1.0);
    }
  `
);

extend({ LiquidMetalMaterial });



function FluidPlane({ color1, color2 }: { color1: string; color2: string }) {
    const meshRef = useRef<THREE.Mesh>(null);
    const materialRef = useRef<any>(null);

    const { viewport } = useThree();

    useFrame((state, delta) => {
        if (materialRef.current) {
            materialRef.current.uTime = state.clock.elapsedTime;
            // Update colors dynamically if props change
            materialRef.current.uColor1 = new THREE.Color(color1);
            materialRef.current.uColor2 = new THREE.Color(color2);
        }

        // Subtle breathing motion for the mesh itself - VERY SLOW
        if (meshRef.current) {
            meshRef.current.rotation.z = Math.sin(state.clock.elapsedTime * 0.05) * 0.02;
        }
    });

    return (
        <mesh ref={meshRef} scale={[viewport.width, viewport.height, 1]}>
            <planeGeometry args={[1.5, 1.5, 128, 128]} /> {/* High poly for smooth ripples */}
            <liquidMetalMaterial ref={materialRef} transparent />
        </mesh>
    );
}

interface HyperHeroProps {
    color1?: string;
    color2?: string;
    initialColor?: string;
}

export default function HyperHero({ color1 = "#A18262", color2 = "#F0F0F0", initialColor }: HyperHeroProps) {
    return (
        <div
            className="absolute inset-0 z-0 w-full h-full transition-colors duration-700"
            style={{ backgroundColor: initialColor || color2 }} // Immediate background color match
        >
            <Canvas
                camera={{ position: [0, 0, 1.5], fov: 75 }}
                dpr={[1, 1.5]} // Reduced from [1, 2] for performance
                gl={{ antialias: false, alpha: true }} // Disabled antialias
            >
                <Suspense fallback={null}>
                    <Environment preset="city" /> {/* For reflections if we add standard materials later */}
                    <FluidPlane color1={color1} color2={color2} />
                    <ambientLight intensity={0.5} />
                    <pointLight position={[10, 10, 10]} intensity={1} />
                </Suspense>
            </Canvas>
        </div>
    );
}

"use client";

import Link from "next/link";
import { ArrowRight } from "lucide-react";

interface ServiceCardProps {
  title: string;
  href: string;
  icon: React.ElementType<{ className?: string }>;
  features: string[];
  variant?: "default" | "emergency";
}

export default function ServiceCard({
  title,
  href,
  icon: Icon,
  features,
  variant = "default",
}: ServiceCardProps) {
  const isEmergency = variant === "emergency";

  if (isEmergency) {
    return (
      <Link
        href={href}
        className="group relative block p-10 h-[420px] flex flex-col justify-between items-center text-center overflow-hidden bg-[#050505] text-white rounded-none border border-white/5 hover:border-red-900/50 transition-colors duration-500"
      >
        {/* Emergency Background Glow */}
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_0%,rgba(220,38,38,0.15),transparent_70%)] opacity-50 group-hover:opacity-100 transition-opacity duration-700" />

