import { useRef, useEffect } from "react";
import HyperHero from "./HyperHero";
import TechSpecs from "./TechSpecs";
import ServiceNavigation from "./ServiceNavigation";
import Link from "next/link";
import { useRouter } from "next/navigation";
import { ArrowRight, ShieldCheck, ChevronDown, Wind } from "lucide-react";
import { motion } from "framer-motion";
import { ServicePageData, serviceData } from "../data/serviceData";
import ServiceDetailSection from "./ServiceDetailSection";

export default function ServicePageLayout({ data }: { data: ServicePageData }) {
    const router = useRouter();

    // Calculate Prev/Next
    const serviceKeys = Object.keys(serviceData);
    const currentIndex = serviceKeys.indexOf(data.slug);
    const prevKey = serviceKeys[(currentIndex - 1 + serviceKeys.length) % serviceKeys.length];
    const nextKey = serviceKeys[(currentIndex + 1) % serviceKeys.length];
    const prevService = serviceData[prevKey];
    const nextService = serviceData[nextKey];

    // Keyboard & Swipe Navigation
    useEffect(() => {
        const handleKeyDown = (e: KeyboardEvent) => {
            if (e.key === 'ArrowLeft') router.push(`/services/${prevKey}`);
            if (e.key === 'ArrowRight') router.push(`/services/${nextKey}`);
        };

        let touchStartX = 0;
        let touchEndX = 0;

        const handleTouchStart = (e: TouchEvent) => {
            touchStartX = e.changedTouches[0].screenX;
        };

        const handleTouchEnd = (e: TouchEvent) => {
            touchEndX = e.changedTouches[0].screenX;
            handleSwipe();
        };

        const handleSwipe = () => {
            if (touchEndX < touchStartX - 50) {
                // Swipe Left -> Next
                router.push(`/services/${nextKey}`);
            }
            if (touchEndX > touchStartX + 50) {
                // Swipe Right -> Previous
                router.push(`/services/${prevKey}`);
            }
        };

        window.addEventListener('keydown', handleKeyDown);
        window.addEventListener('touchstart', handleTouchStart);
        window.addEventListener('touchend', handleTouchEnd);

        return () => {
            window.removeEventListener('keydown', handleKeyDown);
            window.removeEventListener('touchstart', handleTouchStart);
            window.removeEventListener('touchend', handleTouchEnd);
        };
    }, [nextKey, prevKey, router]);

    return (
        <main className={`min-h-screen overflow-x-hidden selection:bg-black selection:text-white ${data.theme.secondaryBg}`}>

            {/* 1. Custom Hero */}
            <section className={`relative h-screen w-full flex items-center justify-center overflow-hidden ${data.theme.secondaryBg}`}>
                <HyperHero
                    color1={data.theme.hero1}
                    color2={data.theme.hero2}
                    initialColor={data.theme.hero2} // Match the shader's base color instantly
                />

                <div className="relative z-10 text-center px-4 max-w-5xl mx-auto">
                    <p className={`font-mono text-xs md:text-sm uppercase tracking-[0.3em] mb-4 md:mb-6 backdrop-blur-sm inline-block px-4 py-2 rounded-full border border-black/5 ${data.theme.primaryText} bg-white/50`}>
                        {data.hero.tag}
                    </p>
                    <h1 className={`text-6xl md:text-9xl font-sans tracking-tighter mb-6 md:mb-8 leading-[0.9] text-slate-900`}>
                        {data.hero.title}
                    </h1>
                    <p className={`text-lg md:text-2xl font-light max-w-xl mx-auto leading-relaxed backdrop-blur-sm text-slate-700`}>
                        {data.hero.description}
                    </p>
                </div>

                {/* Scroll Indicator */}
                <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1, y: [0, 10, 0] }}
                    transition={{ delay: 1, duration: 2, repeat: Infinity, ease: "easeInOut" }}
                    className="absolute bottom-8 md:bottom-12 left-1/2 -translate-x-1/2 text-slate-400"
                >
                    <ChevronDown size={32} strokeWidth={1} />
                </motion.div>
            </section>

            {/* 2. Introduction */}
            <section className={`w-full px-6 md:px-[5vw] py-16 md:py-32 bg-white/50`}>
                <div className="max-w-4xl mx-auto text-center">
                    <p className={`font-mono text-xs uppercase tracking-[0.3em] mb-8 ${data.theme.primaryText}`}>
                        Why Choose Us
                    </p>
                    <h2 className="text-3xl md:text-5xl font-serif text-slate-900 mb-6 leading-tight">
                        {data.intro.heading}
                    </h2>
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-8 pt-12 border-t border-black/5">
                        {data.intro.stats.map((stat, i) => (
                            <div key={i}>
                                <div className={`text-3xl font-bold mb-2 ${data.theme.primaryText}`}>{stat.value}</div>
                                <div className="text-sm uppercase tracking-wide text-slate-500">{stat.label}</div>
                            </div>
                        ))}
                    </div>
                </div>
            </section>

            {/* 3. Detailed Services */}
            <ServiceDetailSection data={data} />

            {/* 3.5 Add-On Service (Optional) */}
            {data.addOn && (
                <section className={`py-16 md:py-24 px-6 md:px-[5vw] ${data.theme.secondaryBg}`}>
                    <div className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-12 items-center">
                        <div>
                            <div className={`inline-flex items-center gap-2 px-3 py-1 bg-white rounded-full text-xs font-bold uppercase tracking-wider mb-6 ${data.theme.primaryText}`}>
                                <Wind className="w-4 h-4" />
                                <span>{data.addOn.tag}</span>
                            </div>
                            <h3 className="text-4xl font-serif text-slate-900 mb-6">{data.addOn.title}</h3>
                            <p className="text-lg text-slate-600 leading-relaxed mb-8">
                                {data.addOn.description}
                            </p>
                            <ul className="space-y-4 mb-8">
                                {data.addOn.benefits.map((benefit, i) => (
                                    <li key={i} className="flex items-center gap-3 text-slate-600">
                                        <div className={`w-2 h-2 rounded-full ${data.theme.primaryBg.replace('bg-', 'bg-')}`} />
                                        {/* Hack to use the bg color directly, but we have primaryBg as "bg-blue-950". 
                                            We probably want the brighter primaryText color for the dot? 
                                            Let's use primaryText but swap "text-" for "bg-". 
                                            Actually, let's just use inline style or class manip. 
                                            Let's just use bg-black for simplicity or specific map. 
                                        */}
                                        <div className={`w-2 h-2 rounded-full bg-slate-400`} />
                                        {benefit}
                                    </li>
                                ))}
                            </ul>
                        </div>
                        <div className="relative aspect-square md:aspect-video bg-white rounded-2xl overflow-hidden shadow-xl">
                            <img
                                src={data.addOn.image}
                                alt={data.addOn.title}
                                className="absolute inset-0 w-full h-full object-cover"
                            />
                        </div>
                    </div>
                </section>
            )}

            {/* 4. Technical Specs */}
            <TechSpecs
                specs={data.techSpecs.grid}
                tools={data.techSpecs.tools}
                details={data.techSpecs.list}
                theme={data.theme}
            />

            {/* 5. Trust & Promise */}
            <section className={`w-full px-6 md:px-[5vw] py-16 md:py-24 bg-white border-t border-black/5`}>
                <div className="max-w-4xl mx-auto flex flex-col items-center text-center">
                    <ShieldCheck className={`w-16 h-16 mb-8 ${data.theme.primaryText}`} />
                    <h3 className="text-4xl font-serif text-slate-900 mb-6">The Dakeek Guarantee</h3>
                    <p className="text-xl text-slate-500 max-w-2xl mb-12">
                        We don't just fix it; we certify it. Every service comes with a full warranty and a direct line to our support team.
                    </p>
                    <Link
                        href="/book"
                        className="w-full md:w-auto group relative inline-flex justify-center items-center gap-4 px-12 py-6 bg-slate-900 text-white overflow-hidden rounded-sm"
                    >
                        <span className="relative z-10 font-mono uppercase tracking-widest text-sm">
                            Book Now
                        </span>
                        <ArrowRight className="relative z-10 w-4 h-4 group-hover:translate-x-1 transition-transform" />
                        <div className={`absolute inset-0 translate-y-full group-hover:translate-y-0 transition-transform duration-500 ease-out ${data.theme.primaryBg}`} />
                    </Link>
                </div>
            </section>

            {/* 6. Navigation */}
            <div className="bg-[#FAFAF9]">
                <ServiceNavigation
                    prev={{ id: prevService.slug, name: prevService.hero.title }}
                    next={{ id: nextService.slug, name: nextService.hero.title }}
                    theme={data.theme}
                />
            </div>
        </main>
    );
}

